def CountVisitedKingdomsCheck(num):
    def f(kingdoms, achievements):
        return len(kingdoms) >= num
    return f

def CentralKingdomCheck(kingdoms, achievements):
    return "k01" in kingdoms and "k02" in kingdoms and "k03" in kingdoms and "k04" in kingdoms and "k05" in kingdoms and "k06" in kingdoms

def LastKingdomCheck(kingdoms, achievements):
    if not achievements.get("the_finishist", False): return False
    if not achievements.get("ancient_puzzles", False): return False
    if not achievements.get("lights_out", False): return False
    if not achievements.get("hooked_metapod", False): return False
    if not achievements.get("delirious", False): return False
    if not achievements.get("yahaha", False): return False
    if not achievements.get("laylah_blessing", False): return False
    if not achievements.get("seaside_maze", False): return False
    if not achievements.get("seaside_stocks", False): return False
    if not achievements.get("joyful_rockets_defeated", False): return False
    if not achievements.get("joyful_pentakill", False): return False
    if not achievements.get("throwback_flash", False): return False
    if not achievements.get("throwback_lost_item", False): return False
    if not achievements.get("verdant_laptop", False): return False
    if not achievements.get("radio", False): return False
    if not achievements.get("itemballs", False): return False
    if not achievements.get("trainers", False): return False
    return True

def KingdomVisitedCheck(k):
    def f(kingdoms, achievements):
        return k in kingdoms
    return f

KINGDOM_REQUIREMENTS = {
    "k01": CountVisitedKingdomsCheck(0),
    "k02": CountVisitedKingdomsCheck(1),
    "k03": CountVisitedKingdomsCheck(1),
    "k04": CountVisitedKingdomsCheck(2),
    "k05": CountVisitedKingdomsCheck(2),
    "k06": CountVisitedKingdomsCheck(2),
    "final": CentralKingdomCheck,
    "last": LastKingdomCheck,
    "pwnage01": CountVisitedKingdomsCheck(1),
    "pwnage02": KingdomVisitedCheck("pwnage01"),
    "pwnage03": KingdomVisitedCheck("pwnage02"),
    "pwnage04": KingdomVisitedCheck("pwnage03")
}

def is_kingdom_accessible(kingdom, kingdoms, achievements):
    return KINGDOM_REQUIREMENTS[kingdom](kingdoms, achievements)

def is_word_filtered(w):
    found = False
    if w in PROFANITY: found = True
    if w.endswith("s") and w[:-1] in PROFANITY: found = True
    if w.endswith("es") and w[:-2] in PROFANITY: found = True
    if w.endswith("es") and w[:-1] in PROFANITY: found = True
    if w.endswith("es") and w[:-3] in PROFANITY: found = True
    if w.endswith("ves") and w[:-3] + "f" in PROFANITY: found = True
    if w.endswith("ves") and w[:-3] + "v" in PROFANITY: found = True
    if w.endswith("ves") and w[:-3] + "fe" in PROFANITY: found = True
    if w.endswith("ves") and w[:-3] + "ve" in PROFANITY: found = True
    if w.endswith("i") and w[:-1] + "us" in PROFANITY: found = True
    if w.endswith("es") and w[:-2] + "is" in PROFANITY: found = True
    if w.endswith("a") and w[:-1] + "on" in PROFANITY: found = True
    return found
